import React, {useState, useEffect} from "react";
import ".//App.css";
import {BrowserRouter,Link, NavLink} from "react-router-dom";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Kuvat from "./Navisivut/Kuvat"
import Navbar from "./Navisivut/Navbar";
import Mökki from "./Navisivut/Mökki";
import { fire } from "./Firebase/config";
import Login from './Login';
import Hero from './hero';





// Nämä alla olevat lisukkeet tarvitsee, jotta voi runnata appia.
// asenna Firebase, react-router-dom, (motion), + muut tulevat lisäosat 

// npm install react-router-dom@5
//npm install
//npm install firebase
function App() {
  const [user, setUser] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState('');
  const [passwordError, setPasswordError] = useState('');
  const [hasAccount, setHasAccount] = useState(false);
  

  const clearInputs = () =>{
    setEmail('');
    setPassword('');
  }

  const clearErrors = () => {
    setEmailError('');
    setPasswordError('');
  }
  
  const handleLogin = () => {
    clearErrors();
    fire
    .auth()
    .signInWithEmailAndPassword(email, password)
    .catch((err) => {
      switch(err.code){
        case "auth/invalid-email":
          case "auth/user-disabled":
            case "auth/user-not-found":
              setEmailError(err.message);
              break;
              case "auth/wrong-password":
              setPasswordError(err.message);
              break;

      }
    });
  };

  const handleSignup = () => {
    clearErrors();
    fire
    .auth()
    .createUserWithEmailAndPassword(email, password)
    .catch((err) => {
      switch(err.code){
        case "auth/email-already-in-use":
          case "auth/invalid-email":
              setEmailError(err.message);
              break;
              case "auth/weak-password":
              setPasswordError(err.message);
              break;

      }
    });
  };

  const handleLogout = () => {
    fire.auth().signOut();
  }

  const authListener = () => {
    fire.auth().onAuthStateChanged((user)=>{
      if(user){
        clearInputs();
        setUser(user);
      }else{
        setUser('');
      }
    });
  };


  useEffect(() =>{
    authListener();
  },[])

  return (
    <div className="App">
    {user? (
        <Hero handleLogout={handleLogout}/>
      ):(
      <Login
        email={email}
        setEmail={setEmail}
        password={password}
        setPassword={setPassword}
        handleLogin={handleLogin}
        handleSignup={handleSignup}
        hasAccount={hasAccount}
        setHasAccount={setHasAccount}
        emailError={emailError}
        passwordError={passwordError}
      />)}
    <Router>
    <div className="content">
      <BrowserRouter>

      
      </BrowserRouter>
      <Switch>
  
      <Navbar/>
        
      </Switch>
    </div>
    </Router>

    </div>
  );

}

export default App;
