import firebase from "firebase/app";
import React, { useState, useEffect } from 'react';


const Muut = () => {
    const useGetData = () => {
    const [tuotteet, setTuotteet] = React.useState([]);
    const db = firebase.firestore();
    React.useEffect(() => {
      db.collection("tuotteet")
        .get()
        .then((querySnapshot) => {
          let arr = [];
          querySnapshot.docs.map((doc) =>
            arr.push({nimi: doc.nimi, kuva: doc.kuva, tiedot: doc.tiedot })
          );
          setTuotteet(arr);
        });
    }, [db]);
    return [tuotteet];
};
const [tuotteet] = useGetData();

return (
    <div>
      <span>Tuotteet</span>
      {tuotteet.map((tuotteet) => (
        <div key={tuotteet.id}>
          <div>
           <h4>{nimi}</h4>
            <img src={kuva}></img>
            <p>{tiedot}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Muut;
