import React from 'react';
import { BrowserRoute, Link, NavLink } from "react-router-dom";
import SignedInLinks from './SignedInLinks'

import '../App.css';
import { fire } from "../Firebase/config";





const Navbar = () => {

    const authListener = () => {
        fire.auth().onAuthStateChanged((user) => {
            if (user) {
                this.setState({ isLoggedIn: true });
            } else {
                this.setState({ isLoggedIn: false });
            }
        });
    };

        authListener();

    const isLoggedIn = this.state.isLoggedIn;
    if (isLoggedIn = true){
    return (

        <nav className="nav-wrapper red darken-3">
            <div className="container">
                <a className="brand-logo">Varausjärjestelmä</a>
                <ul className="right">
                    <li><Link to="/">Koti</Link></li>
                    <li><NavLink to="/Kuvat"> Kuvat</NavLink></li>
                    <li><NavLink to="/Mökki"> Mökki</NavLink></li>
                    <li><NavLink to="/Varaus_lomake"> Varaus</NavLink></li>
                    <li><NavLink to="/Muut"> Muut</NavLink></li>

                </ul>
            </div>
        </nav>
    );
    }
    else
        return(
            <nav className="nav-wrapper red darken-3">
            <div className="container">
                <a className="brand-logo">Varausjärjestelmä</a>
                <ul className="right">
                    <li><Link to="/">Koti</Link></li>
                    <li><NavLink to="/Kuvat"> Kuvat</NavLink></li>
                    <li><NavLink to="/Mökki"> Mökki</NavLink></li>

                </ul>
            </div>
        </nav>

        )
};

Navbar.propTypes = {};

export default Navbar;
