import firebase from "firebase/app";
import React, { useState, useEffect } from 'react';


const Muut = () => {
    const useGetData = () => {
    const [documents, setDocuments] = React.useState([]);
    const db = firebase.firestore();
    React.useEffect(() => {
      db.collection("tuotteet")
        .get()
        .then((querySnapshot) => {
          let arr = [];
          querySnapshot.docs.map((doc) =>
            arr.push({ nimi: doc.nimi, kuva: doc.kuva, tiedot: doc.tiedot})
          );
          setDocuments(arr);
        });
    }, [db]);
    return [documents];
};
const [documents] = useGetData();

return (
    <div>
      <span>Tuotteet</span>
      {documents.map((documents) => (
        <div key={documents.id}>
          <div>
           {doc.nimi}, {doc.kuva}, {doc.tiedot}
          </div>
        </div>
      ))}
    </div>
  );
};

export default Muut;
